import React from 'react';
import { Link } from 'react-router-dom';
import { useHome } from '../../components'

const HomePage = () => {
    const { projects, authState, username } = useHome();

    return (
        <div className="bg-gray-900 min-h-screen flex flex-col items-center justify-center p-4">
            <h1 className="text-4xl text-white mb-6">Projects</h1>

            {authState.isLoggedIn &&
                <h1 className="text-2xl text-blue-500 mb-4">{`Welcome ${username}`}</h1>
            }

            <div className="w-full max-w-2xl bg-gray-800 p-6 rounded-md shadow-xl">
                {projects.map((project, index) => (
                    <div key={index} className="mb-4">
                        <Link to={`/project/${project.projectId}`} className="text-white hover:text-blue-500">
                            <h2 className="text-xl">{project.title}</h2>
                        </Link>
                    </div>
                ))}
            </div>

            {authState.isLoggedIn &&
                <div className="mt-6">
                    <Link to={`addproject`}>
                        <button className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded-md">
                            Add Project
                        </button>
                    </Link>
                </div>
            }
        </div>
    );
};

export default HomePage;
