import React from 'react';

export const Modal = ({ isOpen, onCancel, onConfirm, message }) => {
    if (!isOpen) return null;

    return (
        <div className="fixed top-0 left-0 w-full h-full flex items-center justify-center z-50">
            <div className="bg-white p-4 rounded-md shadow-lg">
                <p>{message || "Are you sure?"}</p>
                <div className="flex justify-end mt-2">
                    <button className="bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded mr-2" onClick={onConfirm}>
                        Yes
                    </button>
                    <button className="bg-gray-300 hover:bg-gray-400 text-black px-3 py-1 rounded" onClick={onCancel}>
                        No
                    </button>
                </div>
            </div>
        </div>
    );
};
