import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from "react-router-dom";
import axios from 'axios';


export const Tasks = () => {
    const { id } = useParams();
    const [task, setTask] = useState({});

    useEffect(() => {
        axios.get(`${process.env.REACT_APP_API_BASE_URL}/tasks/${id}`, {
            headers: {
                'Authorization': `Bearer ${localStorage.getItem('token')}`
            }
        })
            .then((response) => {
                setTask(response.data);
                console.log(response.data);
            })
            .catch((error) => {
                console.error('Error fetching task:', error);
            });
    }, []);

    const priorityColors = {
        'normal': 'bg-green-500',
        'medium': 'bg-yellow-500',
        'urgent': 'bg-red-500'
    };

    const colorClass = priorityColors[task.priority] || 'bg-gray-500'

    return (
        <>
            <div className={`task ${colorClass}`}>
                <div>
                    <h2>{task.title}</h2>
                    <p>{task.taskDetails}</p>
                    <p>{task.priority}</p>
                    <p>{task.userName}</p>  {/* Ensure `userName` is correct and matches backend structure */}
                    <p>{task.deadline}</p>
                </div>
            </div>
        </>
    )
}
