import React from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

export const Navbar = ({ onLogout }) => {
    const { setAuthInfo, authState } = useAuth();
    return (
        <nav className="bg-gray-900 py-4">
            <div className="container mx-auto">
                <ul className="flex space-x-6 justify-center">
                    <li>
                        <Link to="/" className="text-white hover:text-blue-500">Home</Link>
                    </li>
                    {!authState.isLoggedIn ?
                        (<>
                            <li>
                                <Link to="/login" className="text-white hover:text-blue-500">Login</Link>
                            </li>
                            <li>
                                <Link to="/register" className="text-white hover:text-blue-500">Register</Link>
                            </li></>) :
                        (<>
                            <li>
                                <Link to="/profile" className="text-white hover:text-blue-500">Profile</Link>
                            </li>
                            <li>
                                <button onClick={onLogout} className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600">Logout</button>
                            </li>
                        </>)}
                </ul>
            </div>
        </nav>
    );
};
