import '../../styles/output.css';
import React, { useState, useContext } from "react";
import { AuthService } from "../../services/AuthService";
import { useNavigate } from "react-router-dom";
import { useAuth } from '../../context/AuthContext';


function LoginPage() {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const { setAuthInfo } = useAuth();

    let history = useNavigate();

    const login = async () => {
        try {
            const user = await AuthService.login(email, password);

            setAuthInfo({
                username: user.username,
                role: user.role,
                userId: user.userId,
                isLoggedIn: true,
            });
            console.log("success!!")
            history('/')

        } catch (error) {
            alert("Login failed: " + error.message);
        }
    };

    return (
        <div className="bg-gray-900 flex items-center justify-center h-screen p-4">
            <div className="w-full max-w-xl bg-gray-800 p-8 rounded-lg shadow-xl">
                <h1 className="text-3xl text-white font-bold mb-6">Projects</h1>

                {authState.isLoggedIn &&
                    <h2 className="text-xl text-blue-500 mb-4">{`Welcome ${username}`}</h2>
                }

                <div className="space-y-4">
                    {projects.map((project, index) => (
                        <div key={index}>
                            <Link to={`/project/${project.projectId}`} className="text-white hover:text-blue-500 block">
                                {project.title}
                            </Link>
                        </div>
                    ))}

                    {authState.isLoggedIn &&
                        <div className="mt-4">
                            <Link to={`addproject`}>
                                <button className="w-full bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded-md">
                                    Add Project
                                </button>
                            </Link>
                        </div>
                    }
                </div>
            </div>
        </div>
    );
}

export default LoginPage;
