import React from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

export const Navbar = ({ onLogout }) => {
    const { authState } = useAuth();
    console.log(authState.username)
    return (
        <nav className="bg-gray-900 py-4 shadow-md border-l border-r border-b border-white">
            <div className="container mx-auto flex-row justify-between items-center">
                {!authState.isLoggedIn &&
                    <ul className="flex space-x-8">
                        <li>
                            <Link to="/login" className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600">Login</Link>
                        </li>
                        <li>
                            <Link to="/" className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600">Register</Link>
                        </li>
                    </ul>
                }

                {authState.isLoggedIn &&
                    <ul className="flex space-x-8">
                        <li>
                            <Link to="/home" className="text-white hover:text-blue-500">Home</Link>
                        </li>
                        <li>
                            <Link to="/profile" className="text-white hover:text-blue-500">Profile</Link>
                        </li>
                        <li>
                            <p className="text-white">{`Welcome ${authState.username}`}</p>
                        </li>
                        <li>
                            <button onClick={onLogout} className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600">Logout</button>
                        </li>
                    </ul>
                }
            </div>
        </nav>

    );
};
