import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from "react-router-dom";
import axios from 'axios';


export const Tasks = () => {
    const { id } = useParams();
    const [task, setTask] = useState({});

    useEffect(() => {
        axios.get(`${process.env.REACT_APP_API_BASE_URL}/tasks/${id}`, {
            headers: {
                'Authorization': `Bearer ${localStorage.getItem('token')}`
            }
        })
            .then((response) => {
                setTask(response.data);
                console.log(response.data);
            })
            .catch((error) => {
                console.error('Error fetching task:', error);
            });
    }, []);

    const priorityMapping = {
        1: 'normal',
        2: 'medium',
        3: 'urgent'
    };

    let taskPriority = priorityMapping[task.priority];


    let colorClass = '';

    if (taskPriority === 'normal') {
        colorClass = 'bg-green-500';
    } else if (taskPriority === 'medium') {
        colorClass = 'bg-yellow-500';
    } else if (taskPriority === 'urgent') {
        colorClass = 'bg-red-500';
    }


    return (
        <>
            <div className="bg-gray-900 flex items-center justify-center" style={{ height: 'calc(100vh - 60px)' }}>
                <div style={{ border: '4px solid white' }} className={`w-96 p-8 bg-gray-800 rounded-lg shadow-xl ${colorClass}`}>
                    <h2 className="text-3xl text-white font-bold mb-6">{task.title}</h2>
                    <div className="space-y-4 border-2 border-gray-700 p-4 rounded">
                        <p className="text-white">{task.taskDetails}</p>
                        <p className="text-white">{task.priority}</p>
                        <p className="text-white">{task.userName}</p>
                        <p className="text-white">{task.deadline}</p>
                    </div>
                </div>
            </div>
        </>
    );
}
