import React, { useState, useEffect } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import { useParams } from 'react-router-dom';
import axios from 'axios';

export const Project = () => {

    const initialStatusData = {
        'Assigned': { name: 'Assigned', items: [] },
        'In Progress': { name: 'In Progress', items: [] },
        'Completed': { name: 'Completed', items: [] },
    };

    const { id } = useParams();
    const [project, setProject] = useState(null);
    const [statusData, setStatusData] = useState({});


    useEffect(() => {
        axios.get(`${process.env.REACT_APP_API_BASE_URL}/projects`)
            .then(response => {
                setProject(response.data);

            })
            .catch(error => console.error(error));
    }, [id]);


    useEffect(() => {
        axios.get(`${process.env.REACT_APP_API_BASE_URL}/tasks`)
            .then(response => {
                const tasksByStatus = response.data.reduce((acc, task) => {
                    acc[task.statusCategory].items.push(task);
                    return acc;
                }, { ...statusData });
                setStatusData(tasksByStatus);
            })
            .catch(error => {
                if (error.response) {
                    console.log('Response Error: ', error.response.data);
                } else if (error.request) {
                    console.log('Request Error: ', error.request);
                } else {
                    console.log('Error: ', error.message);
                }
            });
    }, []);

    const onDragEnd = (result) => {
        const { source, destination } = result;
        if (!destination) return;

        if (source.droppableId === destination.droppableId) {
            // Reorder items in the same droppable
            const reorderedItems = Array.from(statusData[source.droppableId].items);
            const [movedItem] = reorderedItems.splice(source.index, 1);
            reorderedItems.splice(destination.index, 0, movedItem);

            setStatusData({
                ...statusData,
                [source.droppableId]: {
                    ...statusData[source.droppableId],
                    items: reorderedItems,
                },
            });
        } else {
            // Move item to a different droppable
            const sourceItems = Array.from(statusData[source.droppableId].items);
            const [movedItem] = sourceItems.splice(source.index, 1);
            const destinationItems = Array.from(statusData[destination.droppableId].items);
            destinationItems.splice(destination.index, 0, movedItem);

            setStatusData({
                ...statusData,
                [source.droppableId]: {
                    ...statusData[source.droppableId],
                    items: sourceItems,
                },
                [destination.droppableId]: {
                    ...statusData[destination.droppableId],
                    items: destinationItems,
                },
            });
        }
    };

    return (
        <>
            <DragDropContext onDragEnd={onDragEnd}>
                {Object.entries(statusData).map(([status, data], index) => (
                    <Droppable droppableId={status} key={status}>
                        {(provided) => (
                            <div
                                ref={provided.innerRef}
                                {...provided.droppableProps}
                                style={{ margin: '0 10px', backgroundColor: 'white', width: 250 }}
                            >
                                {data && (
                                    <>
                                        <h3>{data.name}</h3>
                                        {data.items.map((item, index) => (
                                            <Draggable key={item.id} draggableId={item.id.toString()} index={index}>
                                                {(provided) => (
                                                    <div
                                                        ref={provided.innerRef}
                                                        {...provided.draggableProps}
                                                        {...provided.dragHandleProps}
                                                    >
                                                        {item.content}
                                                    </div>
                                                )}
                                            </Draggable>
                                        ))}
                                        {provided.placeholder}
                                    </>
                                )}
                            </div>
                        )}
                    </Droppable>
                ))}
            </DragDropContext>
        </>
    );
};
