import axios from "axios";
import { getUserIdFromToken, decodeToken } from '../services';
import { useAuth, setAuthInfo } from '../context/AuthContext';

export const Profile = {

    async update(field, value, setAuthInfo) {
        const userId = getUserIdFromToken();

        if (!userId) {
            throw new Error("User ID not found in token.");
        }

        try {
            const response = await axios.put(`${process.env.REACT_APP_API_BASE_URL}/users/${userId}`, { [field]: value }, {
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                }
            });

            if (response.data.error) {
                throw new Error(response.data.error);
            }

            const updatedUser = decodeToken();

            // Here, the passed-in setAuthInfo function is used
            setAuthInfo(prevState => ({
                ...prevState,
                username: updatedUser.username
            }));

            return response.data;

        } catch (error) {
            console.error("Update error", error);
            throw error;
        }
    }
};
