import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from "react-router-dom";
import axios from 'axios';


export const Tasks = () => {
    const { id } = useParams();
    const [task, setTask] = useState({});

    useEffect(() => {
        axios.get(`${process.env.REACT_APP_API_BASE_URL}/tasks/${id}`, {
            headers: {
                'Authorization': `Bearer ${localStorage.getItem('token')}`
            }
        })
            .then((response) => {
                setTask(response.data);
                console.log(response.data);
            })
            .catch((error) => {
                console.error('Error fetching task:', error);
            });
    }, []);

    const priorityColors = {
        'normal': 'bg-green-500',
        'medium': 'bg-yellow-500',
        'urgent': 'bg-red-500'
    };

    const colorClass = priorityColors[task.priority] || 'bg-gray-500'

    return (
        <>
            <div className="bg-gray-900 flex items-center justify-center h-screen">
                <div className={`w-96 p-8 ${colorClass} rounded-lg`}>
                    <h2 className="text-3xl text-white font-bold mb-6">{task.title}</h2>
                    <div className="space-y-4">
                        <p className="text-white">{task.taskDetails}</p>
                        <p className="text-white">{task.priority}</p>
                        <p className="text-white">{task.userName}</p>
                        <p className="text-white">{task.deadline}</p>
                    </div>
                </div>
            </div>
        </>
    );
}
