import React, { useState, useEffect } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import { useParams } from 'react-router-dom';
import axios from 'axios';

export const Project = () => {

    const initialStatusData = {
        'Assigned': { name: 'Assigned', items: [] },
        'In Progress': { name: 'In Progress', items: [] },
        'Completed': { name: 'Completed', items: [] },
    };

    const { id } = useParams();
    const [project, setProject] = useState(null);
    const [statusData, setStatusData] = useState(initialStatusData);


    useEffect(() => {
        axios.get(`${process.env.REACT_APP_API_BASE_URL}/projects`)
            .then(response => { setProject(response.data), console.log(response.data) }

            )


            .catch(error => console.error(error));
    }, [id]);




    useEffect(() => {
        axios.get(`${process.env.REACT_APP_API_BASE_URL}/tasks`)
            .then(response => {
                const tasksByStatus = response.data.reduce((acc, task) => {
                    acc[task.status].items.push(task);
                    return acc;
                }, { ...statusData });
                setStatusData(tasksByStatus);
            })
            .catch(error => console.error(error));
    }, []);


    const onDragEnd = (result) => {

    };

    return (
        <>
            <h1>{project.title}</h1>
            <DragDropContext onDragEnd={onDragEnd}>
                {Object.entries(statusData).map(([status, data], index) => (
                    <Droppable droppableId={status} key={status}>
                        {(provided) => (
                            <div
                                ref={provided.innerRef}
                                {...provided.droppableProps}
                                style={{ margin: '0 10px', backgroundColor: 'white', width: 250 }}
                            >
                                <h3>{data.name}</h3>
                                {data.items.map((item, index) => (
                                    <Draggable key={item.id} draggableId={item.id.toString()} index={index}>
                                        {(provided) => (
                                            <div
                                                ref={provided.innerRef}
                                                {...provided.draggableProps}
                                                {...provided.dragHandleProps}
                                            >
                                                {item.content}
                                            </div>
                                        )}
                                    </Draggable>
                                ))}
                                {provided.placeholder}
                            </div>
                        )}
                    </Droppable>
                ))}
            </DragDropContext>
        </>
    );
};
