import { Link } from 'react-router-dom';
import { Draggable } from 'react-beautiful-dnd';

const DND_Task = ({ task, index, status }) => (
    <Draggable key={`${status}-${index}`} draggableId={`${status}-${index}`} index={index}>
        {(provided) => (
            <div
                ref={provided.innerRef}
                {...provided.draggableProps}
                {...provided.dragHandleProps}
                className="bg-gray-700 text-white p-2 m-2 rounded-md hover:bg-gray-600"
            >
                <Link to={`/task/${task.taskId}`}>
                    <p>{task.title}</p>
                    <p>{task.taskDetails}</p>
                </Link>
            </div>
        )}
    </Draggable>
);

export default DND_Task;
