import React from "react";
import { Link } from "react-router-dom";

function Unauthorized() {
  return (
    <div className="flex flex-col items-center justify-center h-screen bg-gray-100">
      <h1 className="text-3xl font-bold mb-4 text-gray-800">Unauthorized</h1>
      <p className="text-xl mb-6 text-gray-600">You must be logged in to view this page.</p>
      <button className="px-5 py-2 bg-blue-600 text-white font-bold rounded hover:bg-blue-500">
        <Link to="/login">Login</Link>
      </button>
    </div>
  );
}

export default Unauthorized;
