import React, { useState, useEffect } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import { useParams } from 'react-router-dom';
import axios from 'axios';

export const Project = () => {

    const { id } = useParams();
    const [project, setProject] = useState(null);

    useEffect(() => {
        api.get(`${process.env.REACT_APP_API_BASE_URL}/projects`)
            .then(response => setProject(response.data))
            .catch(error => console.error(error));
    }, [id]);

    const initialStatusData = {
        'Assigned': { name: 'Assigned', items: [] },
        'In Progress': { name: 'In Progress', items: [] },
        'Completed': { name: 'Completed', items: [] },
    };

    const [statusData, setStatusData] = useState(initialStatusData);
    const [tasksData, setTasksData] = useState([]);

    useEffect(() => {
        axios.get(`${process.env.REACT_APP_API_BASE_URL}/tasks`)
            .then(response => {
                setStatusData(response.data)
            })
            .catch(error => {
                alert(error)
            })

    })

    const onDragEnd = (result) => {

    };

    return (
        <DragDropContext onDragEnd={onDragEnd}>
            {Object.entries(statusData).map(([status, data], index) => (
                <Droppable droppableId={status} key={status}>
                    {(provided) => (
                        <div
                            ref={provided.innerRef}
                            {...provided.droppableProps}
                            style={{ margin: '0 10px', backgroundColor: 'white', width: 250 }}
                        >
                            <h3>{data.name}</h3>
                            {data.items.map((item, index) => (
                                <Draggable key={item.id} draggableId={item.id} index={index}>
                                    {(provided) => (
                                        <div
                                            ref={provided.innerRef}
                                            {...provided.draggableProps}
                                            {...provided.dragHandleProps}
                                        >
                                            {item.content}
                                        </div>
                                    )}
                                </Draggable>
                            ))}
                            {provided.placeholder}
                        </div>
                    )}
                </Droppable>
            ))}
        </DragDropContext>
    );
};
