import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom'; // Import useHistory for redirecting after the post request
import { useAuth } from '../context/AuthContext';
import { getUserIdFromToken } from '../services';

export const AddTask = () => {
    const { authState } = useAuth();
    const [priority, setPriority] = useState('normal');
    const [dueDate, setDueDate] = useState('');
    const [projectId, setProjectId] = useState('');
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [users, setUsers] = useState([]);
    const [selectedUser, setSelectedUser] = useState(null);
    const navigate = useNavigate();
    const userId = authState.userId;

    const params = useParams();

    const priorityLevels = {
        'normal': 1,
        'medium': 2,
        'urgent': 3
    };

    useEffect(() => {
        const fetchUsersAndProjects = async () => {
            try {
                const [usersResponse, projectsResponse] = await Promise.all([
                    axios.get(`${process.env.REACT_APP_API_BASE_URL}/users`, {
                        headers: {
                            'Authorization': `Bearer ${localStorage.getItem('token')}`
                        }
                    }),
                    axios.get(`${process.env.REACT_APP_API_BASE_URL}/projects`, {
                        headers: {
                            'Authorization': `Bearer ${localStorage.getItem('token')}`
                        }
                    })
                ]);

                setUsers(usersResponse.data);
                setSelectedUser(usersResponse.data[0]);
                setProjectId(params.projectId);
            } catch (error) {
                console.error('There was an error!', error);
            }
        };

        fetchUsersAndProjects();
    }, []);



    const handleSubmit = (event) => {
        event.preventDefault();
        const data = {
            Title: title,
            TaskDetails: description,
            Deadline: dueDate,
            Priority: priorityLevels[priority],
            AssignedUserId: selectedUser?.userId,
            UserId: userId,
            ProjectId: projectId
        };

        console.log(`This is data:${data}`)


        axios
            .post(`${process.env.REACT_APP_API_BASE_URL}/tasks`, data
                , {
                    headers: {
                        'Authorization': `Bearer ${localStorage.getItem('token')}`
                    }
                })
            .then((response) => {
                navigate(`/project/${projectId}`);

            })
            .catch((error) => {
                if (error.response) {
                    console.log(error.response.data);
                    console.log(error.response.status);
                    console.log(error.response.headers);
                } else {
                    console.log('Error', error.message);
                }
            })
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Title:</label>
                    <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
                </div>
                <div>
                    <label>Description:</label>
                    <textarea value={description} onChange={(e) => setDescription(e.target.value)} />
                </div>
                <div>
                    <label>Priority:</label>
                    <select value={priority} onChange={(e) => setPriority(e.target.value)}>
                        <option value="normal">General</option>
                        <option value="medium">Rush</option>
                        <option value="urgent">Urgent</option>
                    </select>
                </div>
                <div>
                    <label>Due Date:</label>
                    <input type="date" value={dueDate} onChange={(e) => setDueDate(e.target.value)} />
                </div>
                <div>
                    <label>User:</label>
                    <select value={selectedUser?.userId} onChange={(e) => setSelectedUser(users.find(user => user.userId === e.target.value))}>
                        {users && users.map((user) => (
                            <option key={user.userId} value={user.userId}>
                                {user.userName}
                            </option>
                        ))}
                    </select>
                </div>
                <button type="submit" disabled={selectedUser === null}>Submit</button>
            </form>
        </div>
    );
};
