import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Import useHistory for redirecting after the post request

export const AddTask = () => {
    const [priority, setPriority] = useState('normal');
    const [dueDate, setDueDate] = useState('');
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [users, setUsers] = useState([]);
    const [selectedUser, setSelectedUser] = useState('');
    const history = useNavigate(); // Initialize useHistory


    const handleSubmit = (event) => {
        event.preventDefault();
        const data = {
            Title: title,
            TaskDetails: description,
            Deadline: dueDate,
            Priority: priority,
            AssignedUser: selectedUser,
        };

        axios
            .post(`${process.env.REACT_APP_API_BASE_URL}/tasks`, data, {
                withCredentials: true,
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                },
            })
            .then((response) => {
                history.push("/"); // Redirect to the home page
            });
    };

    useEffect(() => {
        axios
            .get(`${process.env.REACT_APP_API_BASE_URL}/users`)
            .then((response) => {
                setUsers(response.data);
            })
            .catch((error) => {
                console.error('There was an error!', error);
            });
    }, []);

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Title:</label>
                    <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
                </div>
                <div>
                    <label>Description:</label>
                    <textarea value={description} onChange={(e) => setDescription(e.target.value)} />
                </div>
                <div>
                    <label>Priority:</label>
                    <select value={priority} onChange={(e) => setPriority(e.target.value)}>
                        <option value="normal">General</option>
                        <option value="medium">Rush</option>
                        <option value="urgent">Urgent</option>
                    </select>
                </div>
                <div>
                    <label>Due Date:</label>
                    <input type="date" value={dueDate} onChange={(e) => setDueDate(e.target.value)} />
                </div>
                <div>
                    <label>User:</label>
                    <select value={selectedUser} onChange={(e) => setSelectedUser(e.target.value)}>
                        {users.map((user) => (
                            <option key={user.id} value={user.id}>
                                {user.name}
                            </option>
                        ))}
                    </select>
                </div>
                <button type="submit">Submit</button>
            </form>
        </div>
    );
};
