import React from 'react';
import { Link } from 'react-router-dom';
import { useHome } from '../../components'

const HomePage = () => {
    const { projects, authState, username } = useHome();

    return (
        <div className="bg-gray-900 h-screen p-4 flex flex-col items-center justify-start space-y-6">

            <h1 className="text-3xl text-white font-bold mb-6">Projects</h1>

            {authState.isLoggedIn &&
                <h2 className="text-xl text-blue-500 mb-4">{`Welcome ${username}`}</h2>
            }

            <div className="flex flex-col gap-4 items-center">
                {projects.map((project, index) => (
                    <div key={index} className="w-64 bg-gray-800 p-4 rounded-lg shadow-lg">
                        <Link to={`/project/${project.projectId}`} className="text-white hover:text-blue-500 block">
                            <h3 className="font-bold text-lg mb-2">{project.title}</h3>
                        </Link>
                    </div>
                ))}
            </div>

            {authState.isLoggedIn &&
                <div className="mt-4">
                    <Link to={`addproject`}>
                        <button className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded-md">
                            Add Project
                        </button>
                    </Link>
                </div>
            }
        </div>
    );
};

export default HomePage;
