import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Import useHistory for redirecting after the post request

export const AddTask = () => {
    const [priority, setPriority] = useState('normal');
    const [dueDate, setDueDate] = useState('');
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [users, setUsers] = useState([]);
    const [selectedUser, setSelectedUser] = useState(null);
    const history = useNavigate(); // Initialize useHistory

    const priorityLevels = {
        'normal': 1,
        'medium': 2,
        'urgent': 3
    };

    useEffect(() => {
        axios
            .get(`${process.env.REACT_APP_API_BASE_URL}/users`)
            .then((response) => {
                setUsers(response.data);
                setSelectedUser(response.data[0].userId);

            })
            .catch((error) => {
                console.error('There was an error!', error);
            });
    }, []);


    const handleSubmit = (event) => {
        event.preventDefault();
        const selectedUserEmail = users.find(user => user.userId === selectedUser).email;
        const data = {
            Title: title,
            TaskDetails: description,
            Deadline: dueDate,
            Priority: priorityLevels[priority],
            AssignedUser: {
                UserId: selectedUser
            }
        };



        axios
            .post(`${process.env.REACT_APP_API_BASE_URL}/tasks`, data
            )
            .then((response) => {
                history.push("/"); // Redirect to the home page

            })
            .catch((error) => {

                console.log(error.response.data);
                console.log(error.response.status);
                console.log(error.response.headers);
            })
    };



    return (
        <div>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Title:</label>
                    <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
                </div>
                <div>
                    <label>Description:</label>
                    <textarea value={description} onChange={(e) => setDescription(e.target.value)} />
                </div>
                <div>
                    <label>Priority:</label>
                    <select value={priority} onChange={(e) => setPriority(e.target.value)}>
                        <option value="normal">General</option>
                        <option value="medium">Rush</option>
                        <option value="urgent">Urgent</option>
                    </select>
                </div>
                <div>
                    <label>Due Date:</label>
                    <input type="date" value={dueDate} onChange={(e) => setDueDate(e.target.value)} />
                </div>
                <div>
                    <label>User:</label>
                    <select value={selectedUser?.userId} onChange={(e) => setSelectedUser(users.find(user => user.userId === e.target.value))}>
                        {users && users.map((user) => (
                            <option key={user.userId} value={user.userId}>
                                {user.email}
                            </option>
                        ))}
                    </select>
                </div>
                <button type="submit" disabled={selectedUser === null}>Submit</button>
            </form>
        </div>
    );
};
